import cartActionTypes from './cart-types';

const INITIAL_STATE = {
    hidden: true
}

const cardReducer = (state= INITIAL_STATE, action) => {
    switch (action.types) {
        case cartActionTypes.TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: false
            }
        default:
            return state;
    }
}; 

export default cardReducer;
